mapper.select:
SELECT BOARD_TYPE ,CODE_NAME ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT FROM ( SELECT BOARD_TYPE ,CODE_NAME ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD, COM_CODE WHERE CODE_ID = BOARD_TYPE AND CODE_TYPE = 'menu'
AND CODE_ID IN
#{c}
)
WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

mapper.update:
    	UPDATE BOARD
    	SET
	    	BOARD_TITLE = #{boardTitle},
	    	BOARD_COMMENT = #{boardComment}
    	WHERE BOARD_TYPE = #{boardType} 
    	AND BOARD_NUM = #{boardNum}

mapper.delete:
DELETE FROM BOARD
WHERE BOARD_TYPE = #{boardType} 
AND BOARD_NUM = #{boardNum}
